import { logger } from './logger';
import { contextStorage } from './context';
import { createClient } from '@/utils/supabase/server';
import { z } from 'zod';

export type ActionState<T> = {
    data?: T;
    error?: string;
};

/**
 * Wrapper for Server Actions to provide automatic logging, error handling, Zod validation, and context injection.
 */
export function safeAction<TIn, TOut>(
    actionName: string,
    schema: z.Schema<TIn>,
    handler: (data: TIn, context: { userId?: string; teamId?: string; requestId: string }) => Promise<TOut>
) {
    return async (rawData: TIn): Promise<ActionState<TOut>> => {
        const store = contextStorage.getStore();
        const requestId = store?.requestId || 'internal-' + Math.random().toString(36).substring(7);

        return await contextStorage.run({ ...store, requestId, action: actionName }, async () => {
            try {
                // 1. Validation Zod
                const validation = schema.safeParse(rawData);
                if (!validation.success) {
                    const errorMessage = validation.error.issues[0].message;
                    logger.warn({ action: actionName, error: errorMessage, validationErrors: validation.error.issues }, `Validation failed: ${actionName}`);
                    return { error: errorMessage };
                }
                const data = validation.data;

                const supabase = await createClient();
                const { data: { user } } = await supabase.auth.getUser();

                const userId = user?.id;
                const teamId = store?.teamId;

                logger.info({ action: actionName, userId, teamId }, `Action started: ${actionName}`);

                const result = await handler(data, { userId, teamId, requestId });

                logger.info({ action: actionName, userId, teamId }, `Action success: ${actionName}`);

                return { data: result };
            } catch (error: any) {
                logger.error(
                    {
                        action: actionName,
                        error: error.message,
                        stack: process.env.NODE_ENV === 'development' ? error.stack : undefined
                    },
                    `Action failed: ${actionName}`
                );

                return { error: error.message || 'An unexpected error occurred' };
            }
        });
    };
}

import Stripe from 'stripe';

// Initialize Stripe
const stripeKey = process.env.STRIPE_SECRET_KEY;

if (!stripeKey) {
    console.warn('‚ö†Ô∏è STRIPE_SECRET_KEY is not defined');
}

export const stripe = stripeKey ? new Stripe(stripeKey, {
    apiVersion: '2024-06-20' as any,
    typescript: true,
}) : null;

/**
 * Create a Stripe Checkout Session for rent payment
 */
export async function createRentCheckoutSession({
    leaseId,
    amount,
    periodMonth,
    periodYear,
    tenantName,
    tenantEmail,
    propertyAddress,
    successUrl,
    cancelUrl,
}: {
    leaseId: string;
    amount: number; // Amount in FCFA
    periodMonth: number;
    periodYear: number;
    tenantName: string;
    tenantEmail: string;
    propertyAddress?: string;
    successUrl: string;
    cancelUrl: string;
}) {
    if (!stripe) {
        throw new Error('Stripe is not configured');
    }

    // Convert FCFA to EUR (approximate rate: 1 EUR = 656 FCFA)
    // For production, use a proper exchange rate API
    const FCFA_TO_EUR_RATE = 656;
    const amountInEur = Math.round((amount / FCFA_TO_EUR_RATE) * 100); // Stripe expects cents

    const monthNames = [
        'Janvier', 'F√©vrier', 'Mars', 'Avril', 'Mai', 'Juin',
        'Juillet', 'Ao√ªt', 'Septembre', 'Octobre', 'Novembre', 'D√©cembre'
    ];
    const periodLabel = `${monthNames[periodMonth - 1]} ${periodYear}`;

    const session = await stripe.checkout.sessions.create({
        payment_method_types: ['card'],
        mode: 'payment',
        customer_email: tenantEmail,
        line_items: [
            {
                price_data: {
                    currency: 'eur',
                    product_data: {
                        name: `Loyer ${periodLabel}`,
                        description: propertyAddress
                            ? `Paiement loyer - ${propertyAddress}`
                            : `Paiement du loyer pour ${periodLabel}`,
                        metadata: {
                            lease_id: leaseId,
                            period_month: periodMonth.toString(),
                            period_year: periodYear.toString(),
                        },
                    },
                    unit_amount: amountInEur,
                },
                quantity: 1,
            },
        ],
        metadata: {
            type: 'rent_payment',
            lease_id: leaseId,
            period_month: periodMonth.toString(),
            period_year: periodYear.toString(),
            amount_fcfa: amount.toString(),
            tenant_name: tenantName,
        },
        success_url: successUrl,
        cancel_url: cancelUrl,
        locale: 'fr',
    });

    return {
        sessionId: session.id,
        url: session.url,
    };
}

/**
 * Verify and process a completed Stripe payment
 */
export async function processStripeRentPayment(sessionId: string) {
    if (!stripe) {
        throw new Error('Stripe is not configured');
    }

    const session = await stripe.checkout.sessions.retrieve(sessionId, {
        expand: ['payment_intent', 'payment_intent.latest_charge'],
    });

    if (session.payment_status !== 'paid') {
        throw new Error('Payment not completed');
    }

    const metadata = session.metadata;
    if (!metadata || metadata.type !== 'rent_payment') {
        throw new Error('Invalid payment metadata');
    }

    // Extract card details from charge for traceability
    const paymentIntent = typeof session.payment_intent === 'string'
        ? null
        : session.payment_intent;
    const charge = paymentIntent?.latest_charge as Stripe.Charge | undefined;
    const cardDetails = charge?.payment_method_details?.card;

    const amountEurCents = session.amount_total || 0;
    const amountFcfa = parseInt(metadata.amount_fcfa);

    return {
        leaseId: metadata.lease_id,
        periodMonth: parseInt(metadata.period_month),
        periodYear: parseInt(metadata.period_year),
        amountFcfa,
        tenantName: metadata.tenant_name,
        paymentIntentId: paymentIntent?.id || (typeof session.payment_intent === 'string' ? session.payment_intent : undefined),
        // Rich traceability metadata
        traceMeta: {
            provider: 'stripe',
            stripe_session_id: sessionId,
            stripe_payment_intent_id: paymentIntent?.id || null,
            amount_eur_cents: amountEurCents,
            amount_eur: amountEurCents / 100,
            amount_fcfa: amountFcfa,
            exchange_rate: amountFcfa > 0 && amountEurCents > 0
                ? Math.round(amountFcfa / (amountEurCents / 100))
                : 656,
            card_brand: cardDetails?.brand || null,
            card_last4: cardDetails?.last4 || null,
            card_exp_month: cardDetails?.exp_month || null,
            card_exp_year: cardDetails?.exp_year || null,
            currency: 'eur',
            paid_at: new Date().toISOString(),
        },
    };
}

/**
 * Handle Stripe webhook event for rent payments
 */
export async function handleRentWebhookEvent(event: Stripe.Event) {
    switch (event.type) {
        case 'checkout.session.completed': {
            const session = event.data.object as Stripe.Checkout.Session;

            // Only process rent payments
            if (session.metadata?.type !== 'rent_payment') {
                return { processed: false, reason: 'Not a rent payment' };
            }

            const paymentData = {
                leaseId: session.metadata.lease_id,
                periodMonth: parseInt(session.metadata.period_month),
                periodYear: parseInt(session.metadata.period_year),
                amountFcfa: parseInt(session.metadata.amount_fcfa),
                tenantName: session.metadata.tenant_name,
                stripeSessionId: session.id,
                stripePaymentIntentId: typeof session.payment_intent === 'string'
                    ? session.payment_intent
                    : null,
            };

            return {
                processed: true,
                data: paymentData,
            };
        }

        case 'payment_intent.payment_failed': {
            const paymentIntent = event.data.object as Stripe.PaymentIntent;
            console.error('Payment failed:', paymentIntent.id, paymentIntent.last_payment_error?.message);
            return { processed: false, reason: 'Payment failed' };
        }

        default:
            return { processed: false, reason: `Unhandled event type: ${event.type}` };
    }
}

import { stripe } from "@/lib/subscription/stripe";
import { supabaseAdmin } from "@/lib/supabase-admin"; // Utilise la version Admin !

/**
 * Signale √† Stripe qu'une transaction Mobile Money a eu lieu.
 * Stripe ajoutera les frais (ex: 200 FCFA) √† la prochaine facture d'abonnement de l'agence.
 * 
 * Cette fonction utilise le client Admin pour contourner la RLS car elle est g√©n√©ralement appel√©e
 * par un webhook ou une t√¢che de fond sans utilisateur connect√©.
 */
export async function reportMobileMoneyUsage(teamId: string, quantity: number = 1) {
    try {
        // 1. R√©cup√©rer l'ID d'abonnement via Supabase Admin (Bypass RLS)
        const { data: team, error } = await supabaseAdmin
            .from('teams')
            .select('stripe_subscription_id')
            .eq('id', teamId)
            .single();

        if (error || !team || !team.stripe_subscription_id) {
            console.warn(`[Usage Warning] Agence ${teamId} introuvable ou sans abonnement.`);
            return;
        }

        // 2. ID du prix "Metered" (Frais Mobile Money) d√©fini dans ton .env
        const meteredPriceId = process.env.STRIPE_METERED_PRICE_ID;
        if (!meteredPriceId) {
            console.error("STRIPE_METERED_PRICE_ID manquant dans .env");
            return;
        }

        // 3. R√©cup√©rer l'abonnement Stripe
        const subscription = await stripe.subscriptions.retrieve(team.stripe_subscription_id);

        // 4. Trouver l'item correspondant au prix "Metered"
        let subscriptionItemId = subscription.items.data.find(
            (item) => item.price.id === meteredPriceId
        )?.id;

        // 5. Si l'item n'existe pas encore dans l'abonnement, on l'ajoute
        if (!subscriptionItemId) {
            try {
                const updatedSubscriptionItem = await stripe.subscriptionItems.create({
                    subscription: team.stripe_subscription_id,
                    price: meteredPriceId,
                });
                subscriptionItemId = updatedSubscriptionItem.id;
            } catch (addError) {
                console.error(`[Usage Error] Impossible d'ajouter l'item metered pour ${teamId}:`, addError);
                return;
            }
        }

        // 6. Enregistrer l'usage (Incr√©menter le compteur)
        await (stripe.subscriptionItems as any).createUsageRecord(
            subscriptionItemId,
            {
                quantity: quantity,
                timestamp: Math.floor(Date.now() / 1000),
                action: 'increment',
            }
        );

        console.log(`[Usage Success] +${quantity} transaction(s) factur√©e(s) √† l'agence ${teamId}`);

    } catch (error) {
        console.error(`[Usage Error] √âchec du report pour ${teamId}:`, error);
        // Note: On ne bloque pas le user, mais on log l'erreur pour la r√©gularisation manuelle si besoin.
    }
}

/**
 * Fonctions serveur pour le syst√®me de permissions d'√©quipe
 * Dousell Immo - Gestion Locative SaaS
 * 
 * CE FICHIER NE DOIT √äTRE IMPORT√â QUE DANS DES SERVER COMPONENTS OU SERVER ACTIONS
 */

import { createClient } from "@/utils/supabase/server";
import type { TeamRole, UserTeamContext } from "@/types/team";
import { TEAM_PERMISSIONS, TEAM_ROLE_CONFIG, type TeamPermissionKey } from "./team-permissions";

// =====================================================
// FONCTIONS DE R√âCUP√âRATION
// =====================================================

/**
 * R√©cup√®re le contexte d'√©quipe de l'utilisateur connect√©
 * Si l'utilisateur n'a pas d'√©quipe, en cr√©e une personnelle automatiquement
 */
export async function getUserTeamContext(preferredTeamId?: string): Promise<UserTeamContext | null> {
    const supabase = await createClient();

    const {
        data: { user },
    } = await supabase.auth.getUser();

    if (!user) return null;

    // üÜï V√©rifier la pr√©f√©rence d'√©quipe active (cookie)
    if (!preferredTeamId) {
        const { getActiveTeamId } = await import("@/lib/team-switching");
        preferredTeamId = await getActiveTeamId() || undefined;
    }

    // Si une √©quipe pr√©f√©r√©e est d√©finie, la r√©cup√©rer en priorit√©
    if (preferredTeamId) {
        const { data: preferredMembership } = await supabase
            .from("team_members")
            .select(
                `
        team_id,
        role,
        team:teams(
          name, 
          slug,
          subscription_status,
          subscription_trial_ends_at,
          subscription_tier
        )
      `
            )
            .eq("user_id", user.id)
            .eq("team_id", preferredTeamId)
            .eq("status", "active")
            .maybeSingle();

        if (preferredMembership?.team) {
            const teamData = preferredMembership.team;
            const team = (Array.isArray(teamData) ? teamData[0] : teamData) as {
                name: string;
                slug: string;
                subscription_status?: string;
                subscription_trial_ends_at?: string;
                subscription_tier?: string;
            };

            if (team) {
                return {
                    team_id: preferredMembership.team_id,
                    team_name: team.name,
                    team_slug: team.slug,
                    user_role: preferredMembership.role as TeamRole,
                    subscription_status: team.subscription_status as any,
                    subscription_trial_ends_at: team.subscription_trial_ends_at,
                    subscription_tier: team.subscription_tier as any,
                };
            }
        }
    }

    // Utiliser la fonction RPC pour bypass RLS si n√©cessaire
    const { data, error } = await supabase.rpc("get_user_team", {
        p_user_id: user.id,
    });

    if (error || !data || data.length === 0) {
        // Fallback: requ√™te directe avec subscription_status
        const { data: membership } = await supabase
            .from("team_members")
            .select(
                `
        team_id,
        role,
        team:teams(
          name, 
          slug,
          subscription_status,
          subscription_trial_ends_at,
          subscription_tier
        )
      `
            )
            .eq("user_id", user.id)
            .eq("status", "active")
            .maybeSingle();

        if (!membership?.team) {
            // Pas d'√©quipe trouv√©e - retourner null
            // La cr√©ation d'√©quipe se fait explicitement via createPersonalTeam()
            // appel√© depuis auth-redirect.ts quand l'utilisateur a un selected_plan
            console.log(`[getUserTeamContext] No team for user ${user.id}, returning null`);
            return null;
        }

        const teamData = membership.team;
        // Handle potential array return from Supabase
        const team = (Array.isArray(teamData) ? teamData[0] : teamData) as {
            name: string;
            slug: string;
            subscription_status?: string;
            subscription_trial_ends_at?: string;
            subscription_tier?: string;
        };

        if (!team) return null;

        return {
            team_id: membership.team_id,
            team_name: team.name,
            team_slug: team.slug,
            user_role: membership.role as TeamRole,
            subscription_status: team.subscription_status as any,
            subscription_trial_ends_at: team.subscription_trial_ends_at,
            subscription_tier: team.subscription_tier as any,
        };
    }

    // ‚úÖ AM√âLIORATION: Inclure subscription dans le contexte (√©vite un appel DB suppl√©mentaire)
    return {
        team_id: data[0].team_id,
        team_name: data[0].team_name,
        team_slug: data[0].team_slug,
        user_role: data[0].user_role as TeamRole,
        subscription_status: data[0].subscription_status as any,
        subscription_trial_ends_at: data[0].subscription_trial_ends_at,
        subscription_tier: data[0].subscription_tier as any,
    };
}

/**
 * Cr√©e une √©quipe personnelle pour un utilisateur qui n'en a pas.
 * Appel√© explicitement depuis auth-redirect.ts quand l'utilisateur a un selected_plan,
 * ou depuis /pro/start quand l'utilisateur active manuellement son essai.
 */
export async function createPersonalTeam(userId: string, userEmail: string, metadata?: Record<string, unknown>): Promise<UserTeamContext | null> {
    const supabase = await createClient();

    // G√©n√©rer un slug unique
    const slug = `perso-${userId.substring(0, 8)}`;
    const userName = userEmail.split("@")[0] || "Utilisateur";
    const teamName = `Espace de ${userName}`;

    // Extraire le plan des m√©tadonn√©es (inscrit via signup)
    const selectedPlan = metadata?.selected_plan || "starter";
    const trialDays = 14;
    const trialEndsAt = new Date();
    trialEndsAt.setDate(trialEndsAt.getDate() + trialDays);

    try {
        // Cr√©er l'√©quipe avec le plan initial
        const { data: newTeam, error: teamError } = await supabase
            .from("teams")
            .insert({
                name: teamName,
                slug,
                subscription_tier: selectedPlan,
                subscription_status: 'trial',
                subscription_trial_ends_at: trialEndsAt.toISOString()
            })
            .select("id, name, slug, subscription_tier, subscription_status, subscription_trial_ends_at")
            .single();

        if (teamError || !newTeam) {
            console.error("[Auto-Team] Failed to create team:", teamError?.message);
            return null;
        }

        // Ajouter l'utilisateur comme owner
        const { error: memberError } = await supabase
            .from("team_members")
            .insert({
                user_id: userId,
                team_id: newTeam.id,
                role: "owner",
                status: "active",
            });

        if (memberError) {
            console.error("[Auto-Team] Failed to add member:", memberError.message);
            // Rollback: supprimer l'√©quipe cr√©√©e
            await supabase.from("teams").delete().eq("id", newTeam.id);
            return null;
        }

        console.log(`[Auto-Team] Successfully created team "${teamName}" for user ${userId}`);

        return {
            team_id: newTeam.id,
            team_name: newTeam.name,
            team_slug: newTeam.slug,
            user_role: "owner" as TeamRole,
            subscription_tier: newTeam.subscription_tier as any,
            subscription_status: newTeam.subscription_status as any,
            subscription_trial_ends_at: newTeam.subscription_trial_ends_at,
        };
    } catch (err) {
        console.error("[Auto-Team] Unexpected error:", err);
        return null;
    }
}

/**
 * R√©cup√®re le membership d'un utilisateur dans une √©quipe
 * Utilise une fonction RPC SECURITY DEFINER pour bypass RLS
 */
export async function getTeamMembership(
    teamId: string,
    userId?: string
): Promise<{
    role: TeamRole;
    permissions: TeamPermissionKey[];
    customPermissions: Record<string, boolean>;
} | null> {
    const supabase = await createClient();

    if (!userId) {
        const {
            data: { user },
        } = await supabase.auth.getUser();
        if (!user) return null;
        userId = user.id;
    }

    // Essayer d'abord avec la fonction RPC (bypass RLS)
    const { data: roleData, error: rpcError } = await supabase.rpc("get_user_role_in_team", {
        p_team_id: teamId,
        p_user_id: userId,
    });

    if (!rpcError && roleData) {
        const role = roleData as TeamRole;

        // Calculer les permissions effectives
        const permissions = Object.entries(TEAM_PERMISSIONS)
            .filter(([, roles]) => (roles as readonly string[]).includes(role))
            .map(([key]) => key as TeamPermissionKey);

        return {
            role,
            permissions,
            customPermissions: {},
        };
    }

    // Fallback: requ√™te directe (peut √©chouer avec RLS strict)
    const { data, error } = await supabase
        .from("team_members")
        .select("role, custom_permissions, status")
        .eq("team_id", teamId)
        .eq("user_id", userId)
        .single();

    if (error || !data || data.status !== "active") {
        console.log("getTeamMembership fallback failed:", error?.message || "No data");
        return null;
    }

    const role = data.role as TeamRole;

    // Calculer les permissions effectives
    const permissions = Object.entries(TEAM_PERMISSIONS)
        .filter(([, roles]) => (roles as readonly string[]).includes(role))
        .map(([key]) => key as TeamPermissionKey);

    return {
        role,
        permissions,
        customPermissions: (data.custom_permissions as Record<string, boolean>) || {},
    };
}

// =====================================================
// FONCTIONS DE V√âRIFICATION
// =====================================================

/**
 * V√©rifie si l'utilisateur a une permission sp√©cifique dans une √©quipe
 */
export async function hasTeamPermission(
    teamId: string,
    permission: TeamPermissionKey
): Promise<boolean> {
    const membership = await getTeamMembership(teamId);
    if (!membership) return false;

    // V√©rifier permissions custom d'abord
    if (permission in membership.customPermissions) {
        return membership.customPermissions[permission];
    }

    return membership.permissions.includes(permission);
}

/**
 * V√©rifie si l'utilisateur a un r√¥le sp√©cifique
 */
export async function hasTeamRole(
    teamId: string,
    roles: TeamRole | TeamRole[]
): Promise<boolean> {
    const membership = await getTeamMembership(teamId);
    if (!membership) return false;

    const allowedRoles = Array.isArray(roles) ? roles : [roles];
    return allowedRoles.includes(membership.role);
}

/**
 * Guard pour Server Actions - v√©rifie permission et retourne erreur format√©e
 */
export async function requireTeamPermission(
    teamId: string,
    permission: TeamPermissionKey
): Promise<
    | { success: false; error: string }
    | {
        success: true;
        membership: NonNullable<Awaited<ReturnType<typeof getTeamMembership>>>;
        userId: string;
    }
> {
    const supabase = await createClient();
    const {
        data: { user },
    } = await supabase.auth.getUser();

    if (!user) {
        return { success: false, error: "Non connect√©" };
    }

    const membership = await getTeamMembership(teamId, user.id);

    if (!membership) {
        return { success: false, error: "Vous n'√™tes pas membre de cette √©quipe" };
    }

    const hasPermission = await hasTeamPermission(teamId, permission);

    if (!hasPermission) {
        return {
            success: false,
            error: `Permission refus√©e. Votre r√¥le (${TEAM_ROLE_CONFIG[membership.role].label}) ne permet pas cette action.`,
        };
    }

    return { success: true, membership, userId: user.id };
}

/**
 * Guard pour v√©rifier un r√¥le minimum
 */
export async function requireTeamRole(
    teamId: string,
    allowedRoles: TeamRole[]
): Promise<
    | { success: false; error: string }
    | { success: true; role: TeamRole; userId: string }
> {
    const supabase = await createClient();
    const {
        data: { user },
    } = await supabase.auth.getUser();

    if (!user) {
        return { success: false, error: "Non connect√©" };
    }

    const membership = await getTeamMembership(teamId, user.id);

    if (!membership) {
        return { success: false, error: "Vous n'√™tes pas membre de cette √©quipe" };
    }

    if (!allowedRoles.includes(membership.role)) {
        const allowedLabels = allowedRoles
            .map((r) => TEAM_ROLE_CONFIG[r].label)
            .join(", ");
        return {
            success: false,
            error: `Cette action n√©cessite l'un des r√¥les suivants : ${allowedLabels}`,
        };
    }

    return { success: true, role: membership.role, userId: user.id };
}

