/**
 * Incr√©mente le compteur de vues d'une propri√©t√© de mani√®re atomique
 * Utilise la fonction RPC increment_view_count pour garantir la coh√©rence
 * 
 * @param id - ID de la propri√©t√©
 * @returns Le nouveau total de vues, ou null en cas d'erreur
 */
export const incrementView = async (id: string): Promise<number | null> => {
  try {
    const { data, error } = await supabase.rpc("increment_view_count", {
      property_id_param: id,
    });

    if (error) {
      console.error("incrementView error:", error);
      return null;
    }

    return data as number;
  } catch (error) {
    console.error("incrementView unexpected error:", error);
    return null;
  }
};

/**
 * R√©cup√®re des suggestions de lieux pour l'autocompl√©tion
 * @param query - D√©but du nom de ville ou quartier
 */
export const getSearchSuggestions = async (query: string): Promise<string[]> => {
  if (!query || query.length < 2) return [];

  const searchTerm = query.trim();
  const suggestions = new Set<string>();

  try {
    // 1. Chercher dans les propri√©t√©s internes (location->>city)
    const { data: internalData } = await supabase
      .from("properties")
      .select("location")
      .ilike("location->>city", `%${searchTerm}%`)
      .limit(10);

    if (internalData) {
      internalData.forEach((row: any) => {
        if (row.location?.city) {
          suggestions.add(row.location.city);
          // Si le quartier est dispo et matche aussi
          if (row.location.district && row.location.district.toLowerCase().includes(searchTerm.toLowerCase())) {
            suggestions.add(`${row.location.district}, ${row.location.city}`);
          }
        }
      });
    }

    // 2. Chercher dans les annonces externes (city)
    const { data: externalData } = await supabase
      .from("external_listings")
      .select("city, location")
      .or(`city.ilike.%${searchTerm}%,location.ilike.%${searchTerm}%`)
      .limit(10);

    if (externalData) {
      externalData.forEach((row: any) => {
        if (row.city) suggestions.add(row.city);
        // Pour les externes, 'location' est souvent le quartier ou l'adresse compl√®te
        if (row.location) {
          // On essaie de nettoyer un peu si c'est tr√®s long
          const cleanLoc = row.location.split(',')[0].trim();
          if (cleanLoc.length < 40) {
            suggestions.add(row.location);
          }
        }
      });
    }

    // Convertir en tableau, trier et limiter
    return Array.from(suggestions)
      .filter(s => s.toLowerCase().includes(searchTerm.toLowerCase()))
      .sort((a, b) => {
        // Mettre les matches exacts ou qui commencent par le terme en premier
        const aStarts = a.toLowerCase().startsWith(searchTerm.toLowerCase());
        const bStarts = b.toLowerCase().startsWith(searchTerm.toLowerCase());
        if (aStarts && !bStarts) return -1;
        if (!aStarts && bStarts) return 1;
        return a.localeCompare(b);
      })
      .slice(0, 8);

  } catch (error) {
    console.error("getSearchSuggestions error:", error);
    return [];
  }
};

/**
 * R√©sout un slug de ville (ex: "thies-region") en son vrai nom (ex: "Thi√®s Region")
 * Utilise le cache des villes actives via RPC
 */
export const getCityNameFromSlug = async (slug: string): Promise<string | null> => {
  try {
    // On r√©cup√®re toutes les villes actives
    // TODO: Mettre en cache cette r√©ponse si possible pour √©viter trop d'appels
    const { data, error } = await supabase
      .rpc('get_active_cities_and_types');

    if (error || !data) return null;

    // On cherche la premi√®re ville dont le slug correspond
    const match = (data as { city: string }[]).find(item => slugify(item.city) === slug);

    return match ? match.city : null;
  } catch (err) {
    console.error("getCityNameFromSlug error:", err);
    return null;
  }
};


/**
 * R√©cup√®re la liste des villes actives (ayant au moins une annonce approuv√©e)
 * Utilis√© pour le maillage interne (Villes √† proximit√©)
 */
export const getActiveCities = async (): Promise<string[]> => {
  try {
    // Utilise la RPC existante qui retourne { city, type }
    const { data, error } = await supabase.rpc('get_active_cities_and_types');

    if (error || !data) return [];

    // Extraction et d√©duplication des villes
    const citiesSet = new Set<string>();
    (data as { city: string }[]).forEach(item => {
      if (item.city) citiesSet.add(item.city);
    });

    return Array.from(citiesSet).sort();
  } catch (error) {
    console.error("getActiveCities error:", error);
    return [];
  }
};

// Edge Function: Cleanup des permissions temporaires expir√©es
// Ex√©cut√© automatiquement via CRON (toutes les heures)

import { createClient } from "https://esm.sh/@supabase/supabase-js@2";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers":
    "authorization, x-client-info, apikey, content-type",
};

Deno.serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === "OPTIONS") {
    return new Response("ok", { headers: corsHeaders });
  }

  try {
    // Cr√©er le client Supabase avec la cl√© service
    const supabaseUrl = Deno.env.get("SUPABASE_URL")!;
    const supabaseServiceKey = Deno.env.get("SUPABASE_SERVICE_ROLE_KEY")!;

    const supabase = createClient(supabaseUrl, supabaseServiceKey, {
      auth: {
        autoRefreshToken: false,
        persistSession: false,
      },
    });

    console.log("[Cleanup] Starting cleanup job...");

    // Appeler la fonction RPC de cleanup
    const { data, error } = await supabase.rpc("cleanup_expired_permissions");

    if (error) {
      console.error("[Cleanup] Error:", error);
      throw error;
    }

    const deletedCount = data as number;
    console.log(`[Cleanup] Successfully deleted ${deletedCount} expired permissions`);

    // Optionnel: Notifier les utilisateurs dont les permissions ont expir√©
    // (Peut √™tre fait via un trigger PostgreSQL ou ici)

    return new Response(
      JSON.stringify({
        success: true,
        message: `Cleaned up ${deletedCount} expired permission(s)`,
        deletedCount,
        timestamp: new Date().toISOString(),
      }),
      {
        headers: { ...corsHeaders, "Content-Type": "application/json" },
        status: 200,
      }
    );
  } catch (error: any) {
    console.error("[Cleanup] Fatal error:", error);

    return new Response(
      JSON.stringify({
        success: false,
        error: error.message || "Internal server error",
        timestamp: new Date().toISOString(),
      }),
      {
        headers: { ...corsHeaders, "Content-Type": "application/json" },
        status: 500,
      }
    );
  }
});
